<template>
    <ul class="signIn">
        <li>
            <img src="../../assets/login_07.png" alt="">
            <input type="text" name="phone" value="" placeholder="请输入手机号" v-model='phone' slot="right">
            <yd-sendcode
                slot='right'
                v-model="start1"
                second="60"
                storageKey="dashuaibi"
                @click.native="sendCode1"
                initStr="获取验证码"
                runStr="{%s}秒"
                resetStr="重新获取"
            ></yd-sendcode>
        </li>
        <li>
            <img src="data:image/png;base64," alt="">
            <input type="text" name="verificat" value="" placeholder="请输入短信验证码" v-model='verificat' >
        </li>
        <li>
            <img src="../../assets/login_11.png" alt="">
            <input type="password" name="password" value="" placeholder="请设置密码" v-model='password' >
        </li>
        <li>
            <img src="../../assets/login_11.png" alt="">
            <input v-model='repassword' type="password" name="repassword" value="" placeholder="请确认密码" >
        </li>
        <li  class='_border _errorMessage' v-for='(error,index) in errors'>
            {{error}}
        </li>
        <li class='_border'>
             <yd-checkbox v-model="agree">.</yd-checkbox>
             <span id='agree'>同意并遵守<a>《用户协议》</a>和<a>《隐私政策》</a></span>
        </li>
        <li  class='_border _commmitButton' @click='commitLogin'>
            <yd-button class='_immedit' type="primary" shape="circle">{{text}}</yd-button>
        </li>
        <li class='_border'>
            <a @click='gotologin'>已有账号，立即登录</a>
        </li>
    </ul>
</template>

<script>
import Vue from 'vue';
import {Button, ButtonGroup} from 'vue-ydui/dist/lib.rem/button';
import {CheckBox, CheckBoxGroup} from 'vue-ydui/dist/lib.rem/checkbox';
Vue.component(Button.name, Button);
Vue.component(CheckBox.name, CheckBox);
import {testing} from 'script/commonVarite'
import {Toast} from 'vue-ydui/dist/lib.rem/dialog';
import {SendCode} from 'vue-ydui/dist/lib.rem/sendcode';

Vue.component(SendCode.name, SendCode);
Vue.prototype.$dialog = {
    toast: Toast
};
export default {
    name:'signIn',
    directive:{

    },
    data(){
        return {
            agree: false,
            phone: '',
            verificat: '',
            password: '',
            repassword: '',
            start1: false,
            text:"立即注册",
            auth:'',
            invited: ''
        }
    },
    vuerify: {
        phone:{
            test:/^(((1[3456789][0-9]{1})|(15[0-9]{1}))+\d{8})$/,
            message:'手机号输入不正确'
        },
        verificat:{
            test:/^\d{6}$/,
            message:'验证码格式不正确'
        },
        password: {
            test: /\w{6,}/,
            message: '至少六位字符'
        },
        repassword: {
            test (val) {
                return val === this.password
            },
            message: '密码输入不一致'
        }
    },
    watch : {

    },
    computed:{
        errors () {

        }
    },
    metaInfo () {
        return {
            title:'中国儿童音乐网-注册'
        }
    },
    created(){
        setTimeout(()=>{
            this.init()
        },20)
    },
    methods:{
      invitedHandle(){
        if(this.invited!=""){
          let url = testing.address + '/index.php/prize/add';
          this.$http.post(url, {
            'uid': this.invited,
            'type':'reg'
          }, {
            emulateJSON: true
          }).then(data => {
          })
        }
      },
        gotologin(){
            if(window.location.search.indexOf('auth')!=-1){
                this.$router.push({path:`/signIn?`+window.location.search.substring(window.location.search.indexOf('?')+1,window.location.search.length)+``})
            }else{
                this.$router.push({path:"/signIn"})
            }
        },
        init(){
            if(window.location.search.indexOf('auth')!=-1){
                this.$dialog.toast({
                    mes: '登录成功！绑定手机号',
                    timeout: 2000
                })
                this.text='立即绑定'
            }
            this.text='立即注册'

        },
        sendCode1() {
            let reg=/^(((1[3456789][0-9]{1})|(15[0-9]{1}))+\d{8})$/;
            if(!reg.test(this.phone)){
                this.$dialog.toast({
                    mes: '手机号不正确',
                    timeout: 1500
                })
            }else{
                let  url=testing.address+'/index.php/api/sms';
                this.$http.post(url,{
                    'phone':this.phone
                },{
                    emulateJSON:true
                }).then(data=>{
                    if(data.body.status=='ok'){
                      this.$cookie.set('sendSkey',data.body.skey)
                        this.$dialog.toast({
                            mes: '发送短信成功',
                            timeout: 1500
                        })
                        this.start1 = true;
                    }
                },response=>{
                    this.$dialog.toast({
                        mes: '网络状态不佳~',
                        timeout: 1500
                    })
                })
            }

        },
        commitLogin(){
            if (this.$vuerify.check()&&this.agree) {
                //信息正确
                let  url=testing.address+'/index.php/api/user/reg'
                if(window.location.search.indexOf('auth')!=-1){
                    this.auth=window.location.search.substring(window.location.search.indexOf('=')+1,window.location.search.length)
                }else{
                    this.auth="";
                    location.search.split('?')[1] ? this.invited = location.search.split('?')[1] : this.invited = ''
                }
               this.$http.post(url,{
                  'skey':this.$cookie.get('sendSkey'),
                   'phone':this.phone,
                   'verify':this.verificat,
                   'pwd':this.password,
                   'repwd':this.repassword,
                   'agree':this.agree,
                    "auth":this.auth,
                    'inviter': this.invited
               },{
                   emulateJSON:true
               }).then(data=>{
                   if(data.body.msg=="该用户已存在"){
                     this.invitedHandle()
                     this.$cookie.set('skey',data.body.skey)
                       this.$cookie.set('isLogin',data.body.status)
                       this.$dialog.toast({
                           mes: "您已经注册过了哦~去登录吧",
                           timeout: 1500
                       });
                       this.$router.push({
                           path: '/signIn'
                       });
                   }else if(data.body.status=='ok'){
                     this.invitedHandle()
                     this.$cookie.set('skey',data.body.skey)
                       this.$cookie.set('isLogin',data.body.status)
                       this.$dialog.toast({
                           mes: data.body.msg,
                           timeout: 1500
                       });
                       this.$router.push({
                           path: '/signIn'
                       });
                   }else{
                       this.$dialog.toast({
                           mes: data.body.msg,
                           timeout: 1500
                       });
                   }

               })
            }else{
                this.$dialog.toast({
                    mes: '信息有误!请核对信息哦~',
                    timeout: 1500
                });
            }
        }
    }
}
</script>

<style lang="stylus" rel='stylesheet/stylus' scoped>
@import "./login.styl";
</style>









